package day09_array_carry_forward;

/**
 * 
 */
public class A01AmazingSubArrays {

	/**
	 * @param args
	 * 
	 * 
	 * 
	 *             You are given a string S, and you have to find all the amazing
	 *             substrings of S.
	 * 
	 *             An amazing Substring is one that starts with a vowel (a, e, i, o,
	 *             u, A, E, I, O, U).
	 * 
	 *             Input
	 * 
	 *             Only argument given is string S. Output
	 * 
	 *             Return a single integer X mod 10003, here X is the number of
	 *             Amazing Substrings in given the string. Constraints
	 * 
	 *             1 <= length(S) <= 1e6 S can have special characters Example
	 * 
	 *             Input ABEC
	 * 
	 *             Output 6
	 * 
	 *             Explanation Amazing substrings of given string are : 1. A 2. AB
	 *             3. ABE 4. ABEC 5. E 6. EC here number of substrings are 6 and 6 %
	 *             10003 = 6.
	 * 
	 * 
	 * 
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String A = "";
		System.out.println(solve(A));
		
		String B = "ABEC"; 
		System.out.println(solve(B));
		
	}

	public static int solve(String A) {
		int n = A.length();
		int count = 0;
		for (int i = 0; i < n; i++) {
			if (isVowel(A.charAt(i))) {
				count += n - i;
				count = count % 10003;
			}
		}
		return count;
	}

	public static boolean isVowel(char c) {
		c = Character.toLowerCase(c);
		return c == 'a' || c == 'e' || c == 'i' || c == 'o' || c == 'u';
	}

}




